package com.gmugu.happytour.web.listener;import javax.servlet.annotation.WebListener;import javax.servlet.http.HttpSessionEvent;import javax.servlet.http.HttpSessionListener;import java.text.SimpleDateFormat;import java.util.Date;import java.util.Locale;/** * Created by mugu on 16-5-1. */@WebListenerpublic class SessionListenr implements HttpSessionListener {    SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd hh:mm:ss", Locale.CHINA);    @Override    public void sessionCreated(HttpSessionEvent httpSessionEvent) {        System.out.println(format.format(new Date(System.currentTimeMillis())) + " session 创建:" + httpSessionEvent.getSession().getId());    }    @Override    public void sessionDestroyed(HttpSessionEvent httpSessionEvent) {        System.out.println(format.format(new Date(System.currentTimeMillis())) + " session 销毁:" + httpSessionEvent.getSession().getId());    }}